import React, { useState, useEffect } from 'react';
import './App.css';
import MoodSelector from './components/MoodSelector';

function App() {
  const [selectedMood, setSelectedMood] = useState('');
  const [playlist, setPlaylist] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(() => {
    const saved = localStorage.getItem('favoriteSongs');
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    localStorage.setItem('favoriteSongs', JSON.stringify(favorites));
  }, [favorites]);

  const fetchSongs = async (mood) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        `https://itunes.apple.com/search?term=${encodeURIComponent(mood)}&media=music&limit=10`
      );
      if (!response.ok) throw new Error('Failed to fetch');
      const data = await response.json();
      setPlaylist(data.results);
    } catch (err) {
      console.error('Error fetching songs:', err);
      setError('Something went wrong while fetching songs. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleMoodChange = (mood) => {
    setSelectedMood(mood);
    fetchSongs(mood);
  };

  const toggleFavorite = (track) => {
    const exists = favorites.some((fav) => fav.trackId === track.trackId);
    if (exists) {
      setFavorites(favorites.filter((fav) => fav.trackId !== track.trackId));
    } else {
      setFavorites([...favorites, track]);
    }
  };

  const isFavorite = (track) => {
    return favorites.some((fav) => fav.trackId === track.trackId);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>üéß Mood-Based Music Finder</h1>
        <MoodSelector onSelect={handleMoodChange} />

        {error && <p style={{ color: 'red' }}>{error}</p>}
        {isLoading && <p>üéµ Loading songs...</p>}

        <div className="playlist">
          <h2>{selectedMood ? `${selectedMood} Playlist` : 'Select a Mood'}</h2>
          <ul>
            {playlist.map((track) => (
              <li key={track.trackId}>
                <strong>{track.trackName}</strong> by {track.artistName}
                <br />
                {track.previewUrl && (
                  <audio controls src={track.previewUrl}>
                    Your browser does not support the audio element.
                  </audio>
                )}
                <br />
                <button onClick={() => toggleFavorite(track)}>
                  {isFavorite(track) ? 'üíñ Remove Favorite' : 'ü§ç Add to Favorites'}
                </button>
              </li>
            ))}
          </ul>
        </div>

        {favorites.length > 0 && (
          <div className="playlist">
            <h2>‚≠ê Favorite Songs</h2>
            <ul>
              {favorites.map((track) => (
                <li key={track.trackId}>
                  <strong>{track.trackName}</strong> by {track.artistName}
                  <br />
                  {track.previewUrl && (
                    <audio controls src={track.previewUrl}>
                      Your browser does not support the audio element.
                    </audio>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
